class Registration extends BaseRegistration
{
    /**
     * Save Registration
     * @param <type> $array
     */
    public static function saveRegistration($array)
    {
        $r  = new Registration();
        
        foreach ($array as $key => $value) {
            $field = (string) $key;
            $r->$field = $value;            
        }
         
        ZFCore_Utils::log('Add registration: '.var_export($array, true));
        $r->save();        
    }

}